package org.light.myhobby.specs.albumimport org.light.myhobby.specs.BaseSpecimport org.light.myhobby.pages.HomePageimport org.light.myhobby.pages.user.ShowUserPageimport org.light.myhobby.pages.album.CreateAlbumPageimport org.light.myhobby.pages.album.ShowAlbumPage/** * @author Hanna_Aliakseichykava */class CreateAlbumSpec extends BaseSpec {	      //Log in as user    def setup() {        when:        to HomePage		        then:        at HomePage		        when: "Login as User"        loginModule.login(userLogin, password)                then:        at ShowUserPage		        and:        loginModule.loginLabel.contains(userLogin)    }	    def "Create album" () {        given:        def name = "Test Album"        def description = "Test description"		        when:        createAlbumButton.click()		        then:        at CreateAlbumPage		        when: "Create album"        nameField.value(name)        descriptionField.value(description)        createButton.click()                then:        at ShowAlbumPage        nameLabel == name                when: "Go to User Page"        userLink().click()		        then: "Album link is presented"        at ShowUserPage        albumLink(name).displayed		        when: "Go to Album Page"        albumLink(name).click()		        then:        at ShowAlbumPage        nameLabel == name    }}